mapper.select:
SELECT COUNT(*)
FROM g_user
WHERE u_id = #{u_id} AND u_pw = #{u_pw};

SELECT COUNT(*)
FROM g_user
WHERE u_id = #{id};

mapper.delete:
UPDATE g_user
SET u_drop='Y'
WHERE u_id = #{u_id};

mapper.update:
UPDATE g_user
SET u_pw = #{u_pw}, u_name = #{u_name},
u_email = #{u_email}, u_address = #{u_address}, u_phone = #{u_phone}
WHERE u_id = #{u_id};
